(function(){

if (!self.Prism) {
	return;
}

var Languages = {
	'csharp': 'C#',
	'cpp': 'C++'
};
Prism.hooks.add('before-highlight', function(env) {
	var pre = env.element.parentNode;
	if (!pre || !/pre/i.test(pre.nodeName)) {
		return;
	}
	var language = Languages[env.language] || env.language;
	pre.setAttribute('data-language', language);
});

})();
